INSERT INTO Museu (nome, localizacao, descricao, pagina , imagem) VALUES 
('Louvre Museum', 'Paris, França','O Museu do Louvre é o museu nacional da França e o museu de arte mais visitado do mundo.', 'louvre.html','images/museu-do-louvre.jpg'),
('British Museum', 'Londres, Reino Unido', 'O Museu Britânico é dedicado à história e à cultura da humanidade, com uma vasta coleção de artefatos de todo o mundo.', 'british_museum.html','images/British_Museum_from_NE_2.jpeg'),
('Museu de Esposende', 'Esposende, Portugal','O Museu de Esposende apresenta uma coleção diversificada que reflete a história e a cultura da região.' , 'esposende.html','images/museuesposende.jpg'),
('Museu do Prado', 'Madrid, Espanha','O Museu do Prado em Madrid é um dos principais museus de arte do mundo, com uma extensa coleção de pinturas espanholas, italianas e flamengas.', 'prado.html','images/prado.jpg'),
('The Met', 'Nova York, EUA','O Metropolitan Museum of Art, conhecido como "The Met", é um dos maiores e mais importantes museus de arte do mundo, localizado em Nova York.', 'themet.html','images/themet.jpg'),
('Museu do Palácio Nacional', 'Taipei, Taiwan', 'O Museu do Palácio Nacional em Taiwan exibe uma vasta coleção de artefatos históricos chineses, incluindo cerâmicas, pinturas e antiguidades.','museupalnacional.html','images/museupalnacional.jpg'),
('Galeria Nacional de Londres', 'Londres, Reino Unido','A Galeria Nacional em Londres abriga uma impressionante coleção de pinturas de artistas europeus renomados.' ,'galerianacional.html','images/galerialondres.jpg'),
('Musée dOrsay', 'Paris, França','O Musée dOrsay é conhecido por sua vasta coleção de arte impressionista e pós-impressionista.' ,'museudeorsay.html','images/museudeorsay.jpg'),
('Reina Sofia', 'Madrid, Espanha', 'O Museu Reina Sofia é o principal museu de arte moderna da Espanha, conhecido por abrigar a famosa obra "Guernica" de Picasso.','reinasofia.html','images/reinasofia.jpg'),
('Hermitage', 'São Petersburgo, Russia','O Museu Hermitage, em São Petersburgo, Rússia, é um dos maiores e mais antigos museus do mundo, famoso por sua coleção de arte e cultura.' ,'hermitage.html','images/hermitage.jpg'),
('Museu de Arte Moderna', 'Nova York, EUA','O Museu de Arte Moderna (MoMA) em Nova York é um dos museus mais importantes do mundo para arte moderna e contemporânea, com uma vasta coleção de obras de artistas renomados.' ,'museuartemoderna.html','images/artemoderna.jpg'),
('Museu de Xangai', 'Xangai, China','O Museu de Xangai é um dos museus mais importantes da China, com uma rica coleção de artefatos históricos e culturais.' ,'museuxangai.html','images/museuxangai.jpg');
